type ContractStore interface {
	GetABI(ctx context.Context, network string, mainnet string, address string) (string, error)
}

type MockStore struct{}

func NewMockStore() (ContractStore, error) {
	return &MockStore{}, nil
}

func (m *MockStore) GetABI(_ context.Context, _ string, _ string, address string) (string, error) {
	address = strings.ToLower(address)

	switch address {
	case strings.ToUpper("0x06012c8cf97bead5deae237070f9587f8e7a266d"):
		return cryptokittiesABI, nil
	case strings.ToUpper("0x7be8076f4ea4a4ad08075c2508e481d6c946d12b"):
		return openseaABI, nil
	default:
		return "", errors.New("not found")
	}
}
